import React , {useState, useEffect}from 'react';
import Navbar from '../components/Navbar';
import axios from "axios"
import Avatar from "cartoon-avatar"
import "./css/homepage.css"

function HomePage() {
  const [email, setEmail] = useState("")
  const [role, setRole] =  useState("")
  const avatar = Avatar.generate_avatar()

  useEffect(() => {
      const response = axios.get("http://localhost:5700/api/token");
      setEmail(response.data.email);
      setRole(response.data.role);
  },[])
  return (
    <>
      <Navbar/>
      <div className='homepage'>
        <div className='homepage-container' >
          <ul className='home-ul'>
            <li className='home-li'><h1>vous etes connecter : </h1></li>
            <li className='home-li'><img className='home-img' src={avatar} alt="image" /></li>
            <li className='home-li' style={{fontSize: '30px'}}>votre email : {email} </li>
            <li className='home-li' style={{fontSize: '30px'}}> votre role: {role} </li>
          </ul>
        </div>
      </div>
    </>
  )
}

export default HomePage
